import './App.css';
let objectRows = [
  { text: "Hit the gym", checked: true}, 
  { text: "Hit the gym", checked: false}
]
function handleClick(event){
  objectRows.push({text:document.getElementById("myInput").value, checked:false})
} 
function App() {
  let rows = [];
  let objectRows = [
    { text: "Hit the gym", checked: true}, 
    { text: "Hit the gym", checked: false}
]



  for (let i = 0; i < objectRows.length; i++) {
    rows.push(<li class={objectRows[i].checked?"checked":""} >{objectRows[i].text}</li>);
  } 
  return (
    <div className="App">
      <div class="test">
      <div id="myDIV" class="header">
        <h2>My To Do List</h2>
        <input type="text" id="myInput" placeholder="Title..." />
        <span onClick={handleClick} class="addBtn">Add</span>
      </div>
      <ul id="myUL">
        {rows}
      </ul>
      </div>
    </div>
  );
}

export default App;
