import React from 'react';
import { render } from 'react-dom';
import './App.css';

export default class App extends React.Component{
   rows = [];
   objectRows = [
    { text: "Hit the gym", checked: true}, 
    { text: "Hit the gym", checked: false}
  ];


  handleClick(event){
    this.objectRows.push({text:document.getElementById("myInput").value, checked:false})
  } 

  render(){
    this.rows=[];
    for (let i = 0; i < this.objectRows.length; i++) {
      this.rows.push(<li class={this.objectRows[i].checked?"checked":""} >{this.objectRows[i].text}</li>);
    } 
  return (
    <div className="App">
      <div className="test">
      <div id="myDIV" className="header">
        <h2>My To Do List</h2>
        <input type="text" id="myInput" placeholder="Title..." />
        <span onClick={this.handleClick} className="addBtn">Add</span>
      </div>
      <ul id="myUL">
        {this.rows}
      </ul>
      </div>
    </div>
  );
  }

}
render(<App />, document.getElementById('root'));
