import React from 'react';
import { render } from 'react-dom';
import './App.css';

export default class App extends React.Component {
  rows = [];
  objectRows = [
    { text: "Hit the gym", checked: true },
    { text: "Hit the", checked: false }
  ];
  title = "";

  constructor() {
    super();
    // Binding method
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      value: true
    }

  }


  handleClick() {
    if (this.title) {
      this.objectRows.push({ text: this.title, checked: false })
      this.rows = [];
      for (let i = 0; i < this.objectRows.length; i++) {
        this.rows.push(<li key={i} className={this.objectRows[i].checked ? "checked" : ""} >{this.objectRows[i].text}
        <button key={"del"+i} onClick={  () => this.removeTodo(i)} >Del</button>
        </li>);
      }
      this.title = "";
      this.state.value = '';
      this.setState(this.state);
    }
  }
  removeTodo( index ){
    this.rows.splice(index, 1);
    this.objectRows.splice(index, 1);
     this.state.value = '';
      this.setState(this.state);
  }

  render() {
    return (
      <div className="App">
        <div className="test">
          <div id="myDIV" className="header">
            <h2>My To Do List</h2>
            <input type="text" id="myInput" onChange={event => this.title = event.target.value} placeholder="Title..." />
            <span onClick={this.handleClick} className="addBtn">Add</span>
          </div>
          <ul id="myUL">
            {this.rows}
          </ul>
        </div>
      </div>
    );
  }

}
render(<App />, document.getElementById('root'));
